>alert('ERROR: 001 - This record can't be added ...');window.location.href='dashboard.php?page=crud_$table';</script>";}break;default: echo "Program Entered into DEFAULT";}}?><!-- Content Header (Page header) --><section class="content-header"><h1>Master Records<small>Preview</small></h1><ol class="breadcrumb"><li><a href="dashboard.php"><i class="fa fa-dashboard"></i> Home</a></li><li class="active"><?php echo $table; ?> Master Records </li></ol></section><section class="content"><div class="row"><div class="col-md-12"><!------CONTROL TABS START------><div class="nav-tabs-custom"><ul class="nav nav-tabs pull-right"><li><button class="btn btn-block btn-info" href="import.php?action=import&table=<?php echo $table; ?>" target="_blank"><i class="fa fa-upload"></i>&nbsp;&nbsp;Import</button>&nbsp;&nbsp;<li><li><button class="btn btn-block btn-warning" href="printpdf.php?action=print&table=<?php echo $table; ?>" target="_blank"><i class="fa fa-file-pdf-o"></i>&nbsp;&nbsp;PDF</button>&nbsp;&nbsp;<li><li><button class="btn btn-block btn-primary" href="export.php?action=export&table=<?php echo $table; ?>" target="_blank"><i class="fa fa-file-excel-o"></i>&nbsp;&nbsp;CSV</button>&nbsp;&nbsp;</li><li><a href="#add" data-toggle="tab" class="btn bg-green-active"><i class="fa fa-plus-square"></i>&nbsp;&nbsp;New <?php echo $table; ?></a></li><li class="active"><a href="#list" data-toggle="tab" class="btn bg-primary"><i class="fa fa-list"></i>&nbsp;&nbsp;<?php echo $table; ?> List </a></li><li class="pull-left header"><i class="fa fa-th"></i> Manage <?php echo $table; ?><li></ul><!------CONTROL TABS END------><div class="tab-content"><!----TABLE LISTING STARTS--><div class="tab-pane active" id="list"><section class="content"><div class="row"><div class="box border-top-solid"><div class="box-body table-responsive"><table id="example1" class="table table-bordered table-striped "><thead><tr><th>id</th><th>tag_english</th><th>tag_marathi</th><